        //FINALIZE
        [HttpPost("/api/finalizeload")]
        public async Task<ActionResult<Response>> FinalizeLoad(SiloLoad siloLoad)
        {
            try
            {
                siloLoad.Modified = DateTime.Now;
                siloLoad.WayBillNumber = GenerateWayBillNumber();
                siloLoad.IsFinalized = true;
                siloLoad.FinalizedDate = DateTime.Now;
                siloLoad.Modified = DateTime.Now;
                siloLoad.SecondWeightDate = DateTime.Now;
                siloLoad.TotalWeigth = (siloLoad.SecondWeight - siloLoad.FirstWeight);


                _context.Entry(siloLoad).State = EntityState.Modified;
                await _context.SaveChangesAsync();

                var silo = _context.Silos.Where(x => x.Id == siloLoad.SiloId).FirstOrDefault();

                silo.CurrentMeasurement = silo.CurrentMeasurement + siloLoad.TotalWeigth;
                silo.PrecentageFull = Convert.ToInt32((silo.CurrentMeasurement / silo.Capacity) * 100);
                silo.SpaceAvailable = silo.Capacity - silo.CurrentMeasurement;

                _context.Entry(silo).State = EntityState.Modified;
                await _context.SaveChangesAsync();

                //TODO: Generate waybill file 
                return Ok(new Response()
                {
                    success = true,
                    item = siloLoad
                });

            }
            catch (Exception ex)
            {
                return new Response() { success = false, message = ex.Message };
            }

        }

        [HttpPost("/api/finalizeoffload")]
        public async Task<ActionResult<Response>> FinalizeOffLoad(SiloLoad siloLoad)
        {
            try
            {
                siloLoad.Modified = DateTime.Now;
                siloLoad.WayBillNumber = GenerateWayBillNumber();
                siloLoad.IsFinalized = true;
                siloLoad.FinalizedDate = DateTime.Now;
                siloLoad.Modified = DateTime.Now;
                siloLoad.SecondWeightDate = DateTime.Now;
                siloLoad.TotalWeigth = (siloLoad.FirstWeight - siloLoad.SecondWeight);


                _context.Entry(siloLoad).State = EntityState.Modified;
                await _context.SaveChangesAsync();

                var silo = _context.Silos.Where(x => x.Id == siloLoad.SiloId).FirstOrDefault();

                silo.CurrentMeasurement = silo.CurrentMeasurement - siloLoad.TotalWeigth;
                silo.PrecentageFull = Convert.ToInt32((silo.CurrentMeasurement / silo.Capacity) * 100);
                silo.SpaceAvailable = silo.Capacity - silo.CurrentMeasurement;

                _context.Entry(silo).State = EntityState.Modified;
                await _context.SaveChangesAsync();

                //TODO: Generate waybill file 
                return Ok(new Response()
                {
                    success = true,
                    item = siloLoad
                });

            }
            catch (Exception ex)
            {
                return new Response() { success = false, message = ex.Message };
            }

        }

