        [HttpGet("/api/exportreport/{id}")]
        public async Task<ActionResult<Response>> ExportReport(int id)
        {
            try
            {
                List<SiloLoad> siloLoads = new List<SiloLoad>();
                List<Customer> customers = new List<Customer>();

                if (id == 99999)
                {
                    siloLoads = _context.SiloLoads.Include(x => x.Silo).Include(x => x.Product).Include(x => x.Contract).Include(x => x.Vehicle).Where(x => x.Archived == false).OrderByDescending(x => x.Created).ToList();
                }
                else
                {
                    var contracts = _context.Contracts.Where(x => x.CustomerId == id).Select(x => x.Id).ToList();
                    siloLoads = _context.SiloLoads.Include(x => x.Silo).Include(x => x.Product).Include(x => x.Contract).Include(x => x.Vehicle).Where(x => contracts.Contains(x.ContractId.Value) && x.Archived == false).OrderByDescending(x => x.Created).ToList();
                }


                if (siloLoads.Count == 0)
                {
                    return new Response() { success = false, message = "No loads found for customer" };
                }

                customers = _context.Customers.Where(x => x.Archived == false).ToList();

                //Generate Excell
                byte[] excelFile;

                ExcelPackage.LicenseContext = LicenseContext.NonCommercial;

                using (var buf = new System.IO.MemoryStream())
                {
                    using (ExcelPackage package = new ExcelPackage(buf))
                    {

                        ExcelWorksheet ws = package.Workbook.Worksheets.Add("Customer Load Report");


                        //ws.Column(3).Style.Numberformat.Format = "#,###0.00";
                        //ws.Column(4).Style.Numberformat.Format = "#,###0.00";
                        var row = 1;

                        ws.Column(12).Style.Numberformat.Format = "yyyy-mm-dd";
                        ws.Column(13).Style.Numberformat.Format = "yyyy-mm-dd";
                        ws.Column(15).Style.Numberformat.Format = "yyyy-mm-dd";

                        ws.Cells[row, 1].Value = "Reference";
                        ws.Cells[row, 2].Value = "Silo";
                        ws.Cells[row, 3].Value = "Product";
                        ws.Cells[row, 4].Value = "Grading";
                        ws.Cells[row, 5].Value = "Type";

                        ws.Cells[row, 6].Value = "Contract";
                        ws.Cells[row, 7].Value = "Customer";
                        ws.Cells[row, 8].Value = "RegistrationNumber";

                        ws.Cells[row, 9].Value = "FirstWeight";
                        ws.Cells[row, 10].Value = "SecondWeight";
                        ws.Cells[row, 11].Value = "NetWeight";

                        ws.Cells[row, 12].Value = "FirstWeightDate";
                        ws.Cells[row, 13].Value = "SecondWeightDate";

                        ws.Cells[row, 14].Value = "IsFinalized";
                        ws.Cells[row, 15].Value = "FinalizedDate";

                        ws.Row(row).Style.Font.Bold = true;

                        foreach (var load in siloLoads)
                        {
                            var customer = customers.Where(x => x.Id == load.Contract?.CustomerId).FirstOrDefault();

                            var sRow = row + 1;

                            ws.Cells[sRow, 1].Value = load.WayBillNumber;
                            ws.Cells[sRow, 2].Value = load.Silo?.FriendlyName;
                            ws.Cells[sRow, 3].Value = load.Product?.ProductName;
                            ws.Cells[sRow, 4].Value = load.Product?.ProductGrading;
                            ws.Cells[sRow, 5].Value = load.LoadTypeId.ToString();

                            ws.Cells[sRow, 6].Value = load.Contract?.ContractNumber;
                            ws.Cells[sRow, 7].Value = customer != null ? customer.CustomerName : "";
                            ws.Cells[sRow, 8].Value = load.Vehicle?.RegistrationNumber;

                            ws.Cells[sRow, 9].Value = load.FirstWeight;
                            ws.Cells[sRow, 10].Value = load.SecondWeight;
                            ws.Cells[sRow, 11].Value = load.TotalWeigth;

                            ws.Cells[sRow, 12].Value = load.FirstWeightDate;
                            ws.Cells[sRow, 13].Value = load.SecondWeightDate;

                            ws.Cells[sRow, 14].Value = load.IsFinalized ? "Yes" : "No";
                            ws.Cells[sRow, 15].Value = load.FirstWeightDate;

                            row++;
                        }

                        ws.Cells[ws.Dimension.Address].AutoFitColumns();

                        package.Save();
                    }
                    excelFile = buf.ToArray();
                }
                string pdfBase64 = Convert.ToBase64String(excelFile);

                return Ok(new Response()
                {
                    success = true,
                    file = excelFile,
                    message = pdfBase64
                });

            }
            catch (Exception ex)
            {
                return new Response() { success = false, message = ex.Message };
            }

        }

        public string GenerateWayBillNumber()
        {
            string waybill = $"WB000771";
            var latest = _context.SiloLoads.Where(x => x.Archived == false && x.IsFinalized).OrderByDescending(x => x.FinalizedDate).FirstOrDefault();

            if (latest != null)
            {
                try
                {
                    string lastNumberString = latest.WayBillNumber.Substring(2, 6);
                    int lastNumber = Convert.ToInt32(lastNumberString);
                    int newNumber = lastNumber + 1;
                    waybill = $"WB{newNumber.ToString().PadLeft(6, '0')}";
                }
                catch (Exception)
                {
                }
            }

            return waybill;
        }

        private bool SiloLoadExists(int id)
        {
            return _context.SiloLoads.Any(e => e.Id == id);
        }
